-- LONDON, UK (50 hotels)

-- Add room types for all new hotels
SELECT 
  h.id,
  'Standard Room',
  'Comfortable room with all essential amenities for a pleasant stay.',
  2,
  h.price_per_night,
  ARRAY['King Bed', 'TV', 'Air Conditioning', 'Private Bathroom'],
  '/placeholder.svg?height=300&width=400',
  10
FROM hotels h
WHERE h.id NOT IN (SELECT DISTINCT hotel_id FROM room_types);

SELECT 
  h.id,
  'Deluxe Room',
  'Spacious room with premium amenities and enhanced comfort.',
  3,
  h.price_per_night * 1.4,
  ARRAY['King Bed', 'Sofa', 'TV', 'Mini Bar', 'Air Conditioning', 'City View'],
  '/placeholder.svg?height=300&width=400',
  6
FROM hotels h
WHERE h.id NOT IN (SELECT DISTINCT hotel_id FROM room_types WHERE name = 'Deluxe Room');

SELECT 
  h.id,
  'Suite',
  'Luxurious suite with separate living area and premium services.',
  4,
  h.price_per_night * 2,
  ARRAY['King Bed', 'Living Room', 'TV', 'Mini Bar', 'Jacuzzi', 'Premium View', 'Room Service'],
  '/placeholder.svg?height=300&width=400',
  3
FROM hotels h
WHERE h.id NOT IN (SELECT DISTINCT hotel_id FROM room_types WHERE name = 'Suite');

-- Add hotel images for all new hotels
SELECT 
  h.id,
  '/placeholder.svg?height=400&width=600&query=' || REPLACE(LOWER(h.name), ' ', '-') || '-lobby',
  'Hotel Lobby',
  1
FROM hotels h
WHERE h.id NOT IN (SELECT DISTINCT hotel_id FROM hotel_images WHERE display_order = 1);

SELECT 
  h.id,
  '/placeholder.svg?height=400&width=600&query=' || REPLACE(LOWER(h.name), ' ', '-') || '-pool',
  'Swimming Pool',
  2
FROM hotels h
WHERE h.id NOT IN (SELECT DISTINCT hotel_id FROM hotel_images WHERE display_order = 2);

SELECT 
  h.id,
  '/placeholder.svg?height=400&width=600&query=' || REPLACE(LOWER(h.name), ' ', '-') || '-restaurant',
  'Restaurant',
  3
FROM hotels h
WHERE h.id NOT IN (SELECT DISTINCT hotel_id FROM hotel_images WHERE display_order = 3);
